/*global chrome*/
import React, {Component, useState, useEffect} from 'react';
import rp from "request-promise";
import { Button, Card, CardHeader, CardBody, CardFooter} from 'reactstrap';
import ErrorPage from './ErrorPage';
import MaterialPage from './MaterialPage';
import "./css/popup.css";
import ReactDOM from "react-dom";
import { Link, BrowserRouter, Route, Switch } from "react-router-dom";
import {
    Carousel,
    CarouselItem,
    CarouselControl,
    CarouselIndicators,
    CarouselCaption
  } from 'reactstrap';

  const items = [
    {
      id: 1,
      altText: 'Slide 1',
      caption: 'Slide 1'
    },
    {
      id: 2,
      altText: 'Slide 2',
      caption: 'Slide 2'
    },
    {
      id: 3,
      altText: 'Slide 3',
      caption: 'Slide 3'
    }
  ];

function WebScrape() {
    const [url, setUrl] = useState("");
    const [materials, setMaterials] = useState(["NA"]);
    const [numMats, setNumMats] = useState(0);
    const [toggle, setToggle] = useState(true);
    const [noMats, setNoMats] = useState(false);
    const [index, setIndex] = useState(0);const [activeIndex, setActiveIndex] = useState(0);
    const [animating, setAnimating] = useState(false);

    const next = () => {
      if (animating) return;
      const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
      setActiveIndex(nextIndex);
    }

    const previous = () => {
      if (animating) return;
      const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
      setActiveIndex(nextIndex);
    }

    const goToIndex = (newIndex) => {
      if (animating) return;
      setActiveIndex(newIndex);
    }

    const slides = items.map((item) => {
        return (
          <CarouselItem
            className="custom-tag"
            tag="div"
            key={item.id}
            onExiting={() => setAnimating(true)}
            onExited={() => setAnimating(false)}
          >
            <CarouselCaption className="text-danger" captionText={item.caption} captionHeader={item.caption} />
          </CarouselItem>
        );
      });

    useEffect (() => {
      // use the request-promise library to fetch the HTML from
      chrome.tabs.query({active: true, lastFocusedWindow: true}, tabs => {
          if (tabs.length > 0) {
            console.log(tabs);
            let _url = tabs[0].url;
            setUrl(_url);
            rp("http://localhost:8080/" + _url)
                .then(html => {
                    var m = [];
                    //console.log(html);
                    if (html.includes("cotton")) {
                        console.log("cotton");
                        m.push("cotton");
                    }
                    if (html.includes("polyester")) {
                        console.log("polyester");
                        m.push("polyester");
                    }
                    if (html.includes("wool")) {
                        console.log("wool");
                        m.push("wool");
                    }
                    setMaterials(m);
                    setNumMats(m.length);
                    if (m.length > 0) {
                        setNoMats(false);
                    } else {
                        setNoMats(true);
                    }
                })
                .catch(error => setMaterials([]))
            }
      });
    })

    if (materials.length == 0) {
        return (
            <ErrorPage />
        );
    }
    else {
        return (
            <div class="top" >
                <img src={require("./logo.JPG")}></img>
                <div class="carousel">
                    <style>
                    {
                        `.custom-tag {
                            max-width: 50%;
                            height: 500px;
                            background: black;
                        }`
                    }
                    </style>
                    <Carousel
                    activeIndex={activeIndex}
                    next={next}
                    previous={previous}
                    >
                    <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
                    {slides}
                    <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
                    <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
                    </Carousel>
                </div>
            </div>
        );
    } 
  }

  export default WebScrape;
