/*global chrome*/
import React, {Component, useState} from 'react';
import rp from "request-promise";

class WebScraper extends Component {
  constructor() {
    super();
    this.state = {
      url: "",
      materials: []
    };
  }

    componentDidMount() {
      // use the request-promise library to fetch the HTML from pokemon.org
      chrome.tabs.query({active: true, lastFocusedWindow: true}, tabs => {
        console.log("hi");
        console.log(tabs);
        let _url = tabs[0].url;
        this.state.url = _url;
        console.log(this.state.url);
      rp("https://cors-anywhere.herokuapp.com/" + this.state.url)
        .then(html => {
            console.log(html);
            if (html.includes("cotton")) {
                console.log("cotton");
                this.state.materials.append("cotton");
            }
            if (html.includes("polyester")) {
                console.log("polyester");
                this.state.materials.append("polyester");
            }
        })
    });

    }

    render() {
      return (
          <div>
            {this.state.names.map(mat => {
              <li >{mat}</li>
            })}
            <p>hi</p>
          </div>
      );
    }
  }

  export default WebScraper;
