import React, {Component, useState} from 'react';
import rp from "request-promise";

class WebScraper extends Component {
    state = {};

    componentDidMount() {
      // use the request-promise library to fetch the HTML from pokemon.org
      var html = "";
      var url = "https://www.ssense.com/en-ca/men/product/erl/off-white-french-terry-hoodie/5500411"
      rp("https://cors-anywhere.herokuapp.com/" + url)
        .then(_html => {
            html = _html;
            //console.log(_html);
        })
      console.log(html);  
    }

    render() {
      return (
          <div></div>
      );
    }
  }

  export default WebScraper;
